"use strict";
const form = document.querySelector("#myForm");
form.addEventListener("submit", (event) => {
    event.preventDefault();
    const nameElement = document.querySelector("#nameIp");
    const addressElement = document.querySelector("#addressIp");
    const stateElement = document.querySelector("#stateSelect");
    const courseElements = document.querySelectorAll('#course input[type="radio"]:checked');
    const courseValues = Array.from(courseElements).map((element) => element.value);
    const gameElements = document.querySelectorAll('#game input[type="radio"]:checked');
    const gameValues = Array.from(gameElements).map((element) => element.value);
    const dayElement = document.querySelector("#day");
    const monthElement = document.querySelector("#month");
    const yearElement = document.querySelector("#year");
    const name = nameElement.value;
    const address = addressElement.value;
    const state = stateElement.value;
    const day = dayElement.value;
    const month = monthElement.value;
    const year = yearElement.value;
    console.log("Name:", name);
    console.log("Address:", address);
    console.log("State:", state);
    console.log("Course:", courseValues);
    console.log("Game:", gameValues);
    console.log("Day:", day);
    console.log("Month:", month);
    console.log("Year:", year);
});
